import React, { Component } from 'react';
import './App.css';

class App extends Component {
  constructor(props){
    super(props)
      this.state ={
        recipeName: '',
        ingredients:''
  };


      this.handleChange = this.handleChange.bind(this);

      this.handleSubmit = this.handleSubmit.bind(this);
    }


    handleChange(event) {
      this.setState ({recipeName:event.target.recipeName});
      this.setState ({ingredients:event.target.ingredients});

    }

  handleSubmit(event){
    alert('recipe name :' + this.setState.recipeName);
    alert('ingredients :' + this.setState.ingredients);
    event.preventDefault();
  }




  render(){ 
     return ( 
    <form onSubmit={this.handleSubmit}>
        <label>
            Recipe Name: 
            <input type="text" value ={this.recipeName} onChange={this.handleChange}/>
        </label>
        <label>
            Ingredients: 
            <input type="text" value ={this.ingredients} onChange={this.handleChange}/>
        </label>
        <input type="submit" value="Submit" />
    </form>
        );
      }

    }


export default App;
