import React, { Component } from 'react';
import '../components/ViewRecipe';
import axios from 'axios'

class ViewRecipe extends Component {
	constructor(){
		super()
		this.state={
			recipebox:[]
		}
	}
	componentWillMount() {
		this.getItemList();
	}
	componentDidUpdate(){
		this.getItemList()
	}
	getRecipeList() {
		axios.get('/api/recipeBox').then((response) => {
			this.setState({ recipebox: response.data });
		});
	}
	render() {
		const list = this.state.recipebox.map((product, index) => {
			return (
				<ViewRecipe
                getRecipeList={()=>this.getRecipeList()}
					currentProduct={ViewRecipe}
					key={index}
				/>
			);
		});
		return <div>{list}</div>;
	}
}
export default ViewRecipe;
