import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import ToDoList from './components/ToDoList';
import axios from 'axios';
import   "./TodoList.css";

class App extends Component {
  constructor(){
    super()
      this.state ={
        myList : []
      }

  }


componentDidMount (){

  axios.get('/api/toDoList').then((res)=>{

    //console.log(res.data,'')

    this.setState({
      myList :res.data,
    })
  })

}





submitNewTask () {

  axios.post('/api/addtoDoList', {name : this.state.newTask}).then((res)=> {

    console.log(res.data)
    this.setState({
      myList :res.data
    })
 })
}


hanleDeleteItem= i =>{ 
this.setState({
  myList: this.state.myList.filter(myList =>myList.name !=name)
});

};


  render() {

   let toList = this.state.myList.map((name, i)=>{
     return (
       <ul className ="theList">
       <li key = {i} > {name} </li>
       <button onClick={() => {this.onDelete()}}>X</button>
       </ul>
     )
   })

    return (
      <div className="App">

      <div className="header">

      <form onSubmit= {this.addItem}>     

      onDelete ={()=> this.hanleDeleteItem(myList.name)}


     Add new task to the list: <input
          onChange= { (e) => { this.setState({ newTask : e.target.value})} }
           type="text" 
           className="form-control"
           id="taskname"
           value ={this.state.newTask} /> 
          <button type ="submit" className= " btn btn-success btn-block" onClick = { () => {this.submitNewTask()} }> Submit </button> 

          {toList}

      </form>
    </div>
      </div>
    );
  }
}

export default App;
