import React, { Component } from 'react';
import axios from 'axios';
import viewRecipe from '../ViewRecipe/viewRecipe.css'
import Form from '../form/Form';
import { Link } from 'react-router-dom';
import DeleteRecipe from '../DeleteRecipe';


class  ViewRecipe extends Component  {

constructor(){
  super()
  this.state = {
    recipeBox :[]
  }

}


componentDidMount () {

  axios.get('/api/recipeBox').then ((res) => {
     console.log(res.data)
     this.setState ({
      recipeBox : res.data
     })
  })

}






render(){ 



  return ( 

    <div className="viewRecipe">
    <h1> Recipe List</h1>
    <ul> 
    { this.state.recipeBox.map (recipeBox =>
      <li key={recipeBox.id}> {recipeBox.recipeName}<br/> <br/> 
      {recipeBox.ingredients} <br/> <button
						className="product-button"
						onClick={() => {
							axios.delete('/api/recipeBox/' + this.recipeBox.id).then(() => {
								this.recipeBox();
							});
						}}
					>
						{' '}
						Delete
					</button>

      </li>

      )}


    </ul>
</div>
   );
 }

}
export default ViewRecipe;
