import React from 'react';

export default class ViewRecipe extends React.Component {
  render(){


  return (
<div>
  {
    this.props.recipeBox.map (recipeBox =>
            <li key={recipeBox.id}> {recipeBox.recipeName} 
            <br/> <br/> 
           {recipeBox.ingredients} >
     <br/></li>
    )}
  }
</div>
  );
}
}

