import React, { Component } from 'react';
import './App.css';

class App extends Component {
  constructor(props){
    super(props)
      this.state ={
        recipeName: '',
        ingredients:''
  };


      this.handleChange = this.handleChange.bind(this);

      this.handleSubmit = this.handleSubmit.bind(this);
    }


    handleChange(e) {
      this.setState ({
        [e.target.recipeName]: e.target.value
      });
    };

  handleSubmit = e =>{
    e.preventDefault();
    console.log(this.state);
  }




  render(){ 
     return ( 
    <form onSubmit={e => this.onSubmit(e)}>
        <label>
            Recipe Name: 
            <input type="text" value ={this.state.recipeName} onChange={e => this.setState ({recipeName: e.target.value}) }/>
        </label>
        <label>
            Ingredients: 
            <input type="text" value ={this.state.ingredients} onChange={e => this.setState ({ingredients: e.target.value}) }/>
        </label>
        <input type="submit" value="Submit" />
    </form>
        );
      }

    }


export default App;
