import React, { Component } from 'react';
import axios from 'axios';


class  ViewRecipe extends Component  {

constructor(){
  super()
  this.state = {
    recipeBox :[]
  }
}


componentDidMount () {

  axios.get('/api/recipeBox').then ((res) => {
     console.log(res.data)
     this.setState ({
      recipeBox : res.data
     })
  })

}
render(){ 

  let recipeList= this.state.recipeBox.map ((recipeName, i) =>{
    return (
      <li key ={i}> {recipeName}</li>
    )
  })
  return ( 

 <div>{recipeList}</div>
     );
   }

 }
export default ViewRecipe;
