import React, { Component } from 'react';
import axios from 'axios';
import viewRecipe from '../ViewRecipe/viewRecipe.css'

class  ViewRecipe extends Component  {

constructor(){
  super()
  this.state = {
    newRecipe : '',
    newIngredients: ''

}
}

componentDidMount () {

  axios.get('/api/recipeBox').then ((res) => {
     console.log(res.data)
     this.setState ({
      recipeBox : res.data
     })
  })

}


// update Recipe 

updaterecipe(event) {

  const newRecipeBox = {
    recipeName: this.state.newRecipe,
    ingredients:this.state.newIngredients
}

axios.put('/api/AddRecipeBox',newRecipeBox)
}


render(){ 



  return ( 

    <div className="viewRecipe">
    <h1> Recipe List</h1>
    <ul> 
    { this.state.recipeBox.map (recipeBox =>
      <li key={recipeBox.id}> {recipeBox.recipeName}<br/> <br/> 
      {recipeBox.ingredients} <br/> <button onClick ={this.updaterecipe} value={this.state.id}> Edit </button> <button> Delete </button>  <br/></li>

      )}

    </ul>
</div>
   );
 }

}
export default ViewRecipe;
