import React, { Component } from 'react';
import axios from 'axios';


class  ViewRecipe extends Component  {

constructor(){
  super()
  this.state = {
    recipeBox :[]
  }
}


componentDidMount () {

  axios.get('/api/recipeBox').then ((res) => {
     console.log(res.data)
     this.setState ({
      recipeBox : res.data
     })
  })

}
render(){ 



  return ( 

 <div>
    <h1> Recipe List</h1>
    <ul> 
    { this.state.recipeBox.map (recipeBox =>
      <li key={recipeBox.id}> {recipeBox.recipeName}}</li>


      )}
       { this.state.recipeBox.map (recipeBox =>
      <li key={recipeBox.id}> {recipeBox.ingredients}}</li>


      )}
    </ul>
 </div>
     );
   }

 }
export default ViewRecipe;
