import React, { Component } from 'react';
import './App.css';
import Form from './components/form/Form';
import { BrowserRouter as Router, Link} from 'react-router-dom';
import router from './router';

class App extends Component {
  constructor(){
    super()
    this.state = {
      recipeBox :[]
    }
  }



  render(){ 
     return ( 
       <Router>
     <div className = "App">
     <div className="ListWrapper">
     <div className="logo"> </div>

       <nav>
          <ul className="nav-bar">
          <li className="newRecipe"> <Link to='/CreateRecipe' style={{ textDecoration: 'none' }} >Add New recipe</Link></li>
          <br/>
            <li className="ShowRecipe"> <Link to='/ViewRecipe' style={{ textDecoration: 'none' }} >Show Recipes</Link></li>
            <br />
            <li className="removeRecipe"><Link to='/DeleteRecipe' style={{ textDecoration: 'none' }}>Remove Recipe</Link></li> 
          </ul>
        </nav>


        {router}
     </div>
     <Form />

      </div>

      </Router>

        );
      }

    }


export default App;
