import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import ToDoList from './components/ToDoList';
import axios from 'axios';

class App extends Component {
  constructor(){
    super()
      this.state ={
        myList : []
      }

  }


componentDidMount (){

  axios.get('/api/toDoList').then((res)=>{

    //console.log(res.data,'')

    this.setState({
      myList :res.data,
    })
  })

}





submitNewItem () {

  axios.post('/api/addtoDoList', {name : this.state.newitem}).then((res)=> {

    console.log(res.data)
    this.setState({
      myList :res.data
    })
 })
}



  render() {

   let toList = this.state.myList.map((name, i)=>{
     return (
       <li key = {i} > {name} </li>
     )
   })

    return (
      <div className="App">
      <h1> My list </h1>
      {toList}


     Add new item to the list: <input
          onChange= { (e) => { this.setState({ newitem : e.target.value})} }
           type="text" /> 

          <button onClick = { () => {this.submitNewItem()} }> press me </button>

      </div>
    );
  }
}

export default App;
