import React, { Component } from 'react';
import axios from 'axios';
import viewRecipe from '../ViewRecipe/viewRecipe.css'

class  ViewRecipe extends Component  {

constructor(){
  super()
  this.state = {
    recipeBox :[]
  }
}


componentDidMount () {

  axios.get('/api/recipeBox').then ((res) => {
     console.log(res.data)
     this.setState ({
      recipeBox : res.data
     })
  })

}


updateItem() {
  const editedItem = {
    name: this.state.recipeName,
    price: this.state.ingredients
  };

  axios.put('/api/inventory/' + this.state.currentId, editedItem).then(() => {
  });
}
render(){ 



  return ( 

 <div className="viewRecipe">
      <h1> Recipe List</h1>
      <ul> 
      { this.state.recipeBox.map (recipeBox =>
        <li key={recipeBox.id}> {recipeBox.recipeName}<br/> <br/> 
        {recipeBox.ingredients} <br/> <input type="text" onChange = {this.updateItem} value={this.state.recipeBox.recipeName} />
 <br/></li>
        )}

      </ul>
 </div>
     );
   }

 }
export default ViewRecipe;
