import React, { Component } from 'react';
import axios from 'axios';
import viewRecipe from '../ViewRecipe/viewRecipe.css'

class  ViewRecipe extends Component  {

// constructor(){
//   super()
//   this.state = {
//     recipeBox :[]
//   }
// }


componentDidMount () {

  axios.get('/api/recipeBox').then ((res) => {
     console.log(res.data)
     this.setState ({
      recipeBox : res.data
     })
  })

}

componentDidUpdate(props){
  if(props.match.params.id != this.props.match.params.id){
      this.setState({
          name: "",
    price: "",
image_url: "",
      currentId: null,
      })
  }
}

updateItem() {
  const editedItem = {
    name: this.state.name,
    price: this.state.price,
    image_url: this.state.image_url
  };

  axios.put('/api/inventory/' + this.state.currentId, editedItem).then(() => {
  });
}
render(){ 



  return ( 

 <div className="viewRecipe">
      <h1> Recipe List</h1>
      <ul> 
      { this.state.recipeBox.map (recipeBox =>
        <li key={recipeBox.id}> {recipeBox.recipeName}<br/> <br/> 
        {recipeBox.ingredients} <br/> <button onClick={this.updateItem()}>edit</button> <br/></li>

        )}

      </ul>
 </div>
     );
   }

 }
export default ViewRecipe;
