import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import ToDoList from './components/ToDoList';
import axios from 'axios';
import TodoList from "./src/TodoList"

class App extends Component {
  constructor(){
    super()
      this.state ={
        myList : []
      }

  }


componentDidMount (){

  axios.get('/api/toDoList').then((res)=>{

    //console.log(res.data,'')

    this.setState({
      myList :res.data,
    })
  })

}





submitNewTask () {

  axios.post('/api/addtoDoList', {name : this.state.newTask}).then((res)=> {

    console.log(res.data)
    this.setState({
      myList :res.data
    })
 })
}



  render() {

   let toList = this.state.myList.map((name, i)=>{
     return (
       <li key = {i} > {name} </li>
     )
   })

    return (
      <div className="App">
      <form onSubmit= {this.onSubmit}>
      <div className ="from-group">

      <label> Task Name </label>
      {toList}


     Add new item to the list: <input
          onChange= { (e) => { this.setState({ newTask : e.target.value})} }
           type="text" 
           className="form-control"
           id="taskname"
           value ={this.state.newTask} /> 
          </div>
          <button type ="submit" className= " btn btn-success btn-block" onClick = { () => {this.submitNewTask()} }> Submit </button> 


      </form>


      </div>
    );
  }
}

export default App;


