import React from 'react';
import form from '../form/form.css';
import axios from 'axios';

export default class Form extends React.Component {
    state = {
        recipeBox : [],
        newRecipe : ''
  };



    change = e => {
      this.setState ({

        [e.target.name]: e.target.value

      });
    };

    onSubmit = e => {
        console.log(this.state)
        e.preventDefault();
        this.setState  ({
            recipeName: "",
            ingredients:""
        })

        axios.post('/api/recipe-box',e.target.value)
}


    render() {
    return ( 
        <div className="container">

        <form >

                <input
                type = "text"
                        placeholder =" Recipe Name"
                        name="recipeName"
                        value ={this.state.recipeName} 
                        onChange={e => this.change(e) }
                    />

         <br/>



                    <textarea 
                    placeholder ="Ingredients  "
                    type="textarea"
                     name="ingredients" 
                      value ={this.state.ingredients} 
                        onChange={e => this.change(e) }
                    />

               <br/>

               <button className ="submitBtn"onClick ={e => this.onSubmit(e)} > Submit </button>
               <br />
               <br />
               <button className ="cancelBtn" onClick ={e => this.onSubmit(e)} > Cancel </button>
        </form>
        </div>
    );

    }
}
