import React, { Component } from 'react';
import './App.css';
import Form from './components/form/Form';

class App extends Component {
  constructor(){
    super()
    this.state = {
      recipeBox :[]
    }
  }

  componentDidMount (){

    fetch ('/recipeBox')
    .then(res => res.json())
    .then (recipeBox =>this.setState({recipeBox}));
  }

  render(){ 
     return ( 
     <div className = "App">
     <div className="ListWrapper">
     <div className="logo"> </div>
     <div className="newRecipe"> Add New Recipe</div>
     <br/>

     <div className="ShowRecipe">Show Recipes</div>
     <br />


     <div className="removeRecipe">Remove Recipe</div>



     </div>
         <Form />
      </div>
        );
      }

    }


export default App;
